
public class Grade {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    int score=90;
        if(score>=91 && score<=100)
        {
        	System.out.println("He/She scored A grade");
        }
        else if(score<=90 && score>=81)
        {
        	System.out.println("He/She scored B grade");
        }
        else
        {
        	System.out.println("He/She scored other than A and B grade");
        }
	}

}
